SELECT rsr.rule_id AS ruleId,rsr.platform AS platform, r.rule_name AS ruleName, r.rule_code AS ruleCode, r.risk_level AS riskLevel, COUNT(*) AS count FROM rule_scan_result rsr INNER JOIN rule r ON rsr.rule_id = r.id WHERE 1=1
AND rsr.tenant_id = #{tenantId}
AND r.risk_level = #{riskLevel} AND r.status = "valid" AND rsr.status IN #{item} GROUP BY rsr.rule_id HAVING COUNT > 0 ORDER BY COUNT DESC LIMIT #{limit}

SELECT /*+ query_timeout(30000000)*/
COUNT(*) AS totalRiskCount,
SUM(CASE WHEN r.risk_level = 'High' THEN 1 ELSE 0 END) AS highLevelRiskCount,
SUM(CASE WHEN r.risk_level = 'Medium' THEN 1 ELSE 0 END) AS mediumLevelRiskCount,
SUM(CASE WHEN r.risk_level = 'Low' THEN 1 ELSE 0 END) AS lowLevelRiskCount
FROM rule_scan_result rsr
INNER JOIN
rule r
ON
r.id = rsr.rule_id
