mapper.delete:
UPDATE rule_scan_result
SET ignore_reason_type = null,
    ignore_reason      = null,
    status             = #{status}
WHERE id = #{id}

DELETE
FROM rule_scan_result
WHERE rule_id = #{ruleId}
  AND tenant_id = #{tenantId}

